function Footer() {
  const router = useRouter()
  useEffect(() => {
    // Prefetch the dashboard page
    router.prefetch('/chat')
    router.prefetch('/feed')
    router.prefetch('/listen')
  }, [])

  return (
    <div className=" flex justify-center h-[70px]">
      {' '}
      <nav
        className="w-full md:hidden justify-around items-center   bg-bgGray flex border-t border-solid border-slate-700 
		"
      >
        {/* Bottom Icon Navigation Menu */}
        {routes.map((route: RoutesType, index: number) => (
          <Link href={route.link} key={index}>
            <div className="flex flex-col items-center justify-between  capitalize  ">
              {router.asPath === route.link ? route.activeIcon : route.icon}
              <span
                className={`text-xs font-medium whitespace-nowrap ${sideBarActive(
                  router,
                  route.link,
                )}`}
              >
                {route.title}
              </span>
            </div>
          </Link>
        ))}
      </nav>
    </div>
  )
}

export default Footer
